<?phpdefine('CURSCRIPT', 'akintor');require_once ('../include/common.inc.php');require_once ('./category.php');$T->load('{{ include("../templates/default/akintor.tpl") }}');if(isset($_GET['cat']) && !empty($_GET['cat'])) {$category=$jevix->parse($_GET['cat'], $errors);$rs = $db->query("SELECT tid,title,addtime,size,clickcount FROM xbt_torrents WHERE MATCH (`title`, `description`) AGAINST ('.$category.') AND ban=0 LIMIT 0,4");//$rs = $db->query("SELECT tid,title,size,clickcount FROM xbt_torrents WHERE category =".$category." AND ban=0 LIMIT 0,4");if($rs!=false){foreach($rs as $row){    $tmp_tid = $row["tid"];	$tmp_title = cutStr($row["title"], 55);	$tmp_addtime = $row["addtime"];	$tmp_size = $row["size"];	$tmp_clickcount = $row["clickcount"];$T->block('/akintor', array('tid' => $tmp_tid,'title' => $tmp_title,'addtime' => $tmp_addtime,'size' => file_size($tmp_size),'clickcount' => $tmp_clickcount), TRUE); }}}$db->disconnect();$T->display();?>