<?phpdefine('CURSCRIPT', 'getsele');require_once ('../include/common.inc.php');$T->load('{{ include("../templates/default/getsele.tpl") }}');$e_hash = addslashes(pack("H*", antixss($_GET['hash'])));$data = array(":hash|$e_hash|STR");$rows = $db->query_secure("SELECT leechers, seeders FROM xbt_files WHERE info_hash=:hash;", $data, true, false);if($rows!=false){foreach($rows as $row){$leechers=intval($row["leechers"]);$seeders =intval($row["seeders"]);$T->block('/fileinfo', array('leechers' => $leechers ,'seeders' => $seeders ,'peers' => $leechers + $seeders), TRUE); }}$db->disconnect();$T->display();?>